import React, { useState } from 'react';
import { Link } from 'react-router-dom';

function Header() {
  // State pour gérer la langue
  const [language, setLanguage] = useState('fr'); // Par défaut, la langue est en français

  // Fonction pour changer la langue
  const handleChangeLanguage = () => {
    const newLanguage = language === 'fr' ? 'ar' : 'fr'; // Basculer entre le français ('fr') et l'arabe ('ar')
    setLanguage(newLanguage);
  };

  // Fonction pour gérer la recherche
  const handleSearch = (event) => {
    const searchQuery = event.target.value; // Récupérer la valeur de la recherche
    // Ajoutez votre logique de recherche ici
    console.log('Recherche:', searchQuery);
  };

  return (
    <header>
      <a className="logo">
        <div className="logoWrapper">
          <img src='../../public/images/royaume-du-maroc.png' alt='شعار وزارة الداخلية ولاية اكادير' />
        </div>
      </a>

      <nav>
        <ul className="nav-links">
          <li>
            <Link to="/">الرئيسية</Link>
          </li>
          <li>
            <Link to="/demande-certificat">خدمات</Link>
          </li>
          <li>
            <Link to="/demande-certificat">إجراءات</Link>
          </li>
          <li>
            <Link to="/demande-certificat">أسئلة و أجوبة</Link>
          </li>
          {/* Ajoutez d'autres liens de navigation ici */}
        </ul>
      </nav>

      {/* Bouton pour changer la langue */}
      <button onClick={handleChangeLanguage}>
        Changer la langue ({language === 'fr' ? 'Français' : 'العربية'})
      </button>

      {/* Barre de recherche */}
      <input type="text" placeholder="Rechercher..." onChange={handleSearch} />
    </header>
  );
}

export default Header;
