import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import '../Styles/AccueilHeader.css'; // Import the CSS file

function Header() {
  const [language, setLanguage] = useState('ar');

  const handleChangeLanguage = () => {
    const newLanguage = language === 'ar' ? 'fr' : 'ar';
    setLanguage(newLanguage);
  };

  const handleSearch = (event) => {
    const searchQuery = event.target.value;
    console.log('Recherche:', searchQuery);
  };

  return (
    <header className="header">
      <a className="logo">
        <div className="logoWrapper">
          <img src={process.env.PUBLIC_URL + '/images/royaume-du-maroc.png'} alt='شعار وزارة الداخلية ولاية اكادير' />
        </div>
      </a>

      <nav>
        <ul className="nav-links">
          <li>
            <Link to="/">الرئيسية</Link>
          </li>
          <li>
            <Link to="/demande-certificat">خدمات</Link>
          </li>
          <li>
            <Link to="/demande-certificat">إجراءات</Link>
          </li>
          <li>
            <Link to="/demande-certificat">أسئلة و أجوبة</Link>
          </li>
        </ul>
      </nav>

      <button onClick={handleChangeLanguage}>
        AR ({language === 'ar' ?  'العربية':'Français' })
      </button>

      <input className="search-input" type="text" placeholder="Rechercher..." onChange={handleSearch} />
    </header>
  );
}

export default Header;
